import React, { useState, useRef, useEffect } from 'react';
import { Container, Box, Typography, TextField, Button, Paper } from '@mui/material';
import Message from './Message';
import SendIcon from '@mui/icons-material/Send';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null); // Ref für das Ende der Nachrichten

  const handleSendMessage = () => {
    if (input.trim() !== '') {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');

      // Simuliere eine Antwort des Bots
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: "I'm a chatbot! How can I assist you?", sender: 'bot' },
        ]);
      }, 1000);
    }
  };

  // Effekt, um nach unten zu scrollen, wenn sich die Nachrichten ändern
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]); // Diese Funktion wird immer ausgeführt, wenn sich die Nachrichten ändern

  return (
    <Container
      style={{
        display: 'flex',
        flexDirection: 'column',
        height: '100vh',
        width: '100vw',
        padding: '0',
        margin: '0',
      }}
    >
      <Paper
        style={{
          display: 'flex',
          flexDirection: 'column',
          height: '100%',
          width: '100%',
          padding: '20px',
          boxSizing: 'border-box',
        }}
      >
        <Typography variant="h4" gutterBottom align="center">
          SüdtirolKI
        </Typography>

        {/* Box für die Nachrichten */}
        <Box
          style={{
            flex: 1,
            overflowY: 'auto',
            marginBottom: '16px',
          }}
        >
          {messages.map((msg, index) => (
            <Message key={index} message={msg} />
          ))}
          {/* Das unsichtbare Element, auf das gescrollt wird */}
          <div ref={messagesEndRef} />
        </Box>

        {/* Box für das Textfeld und den Button */}
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <TextField
            fullWidth
            variant="outlined"
            label="Type a message"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            style={{ marginRight: '10px' }}
          />
          <Button variant="contained" color="primary" startIcon={<SendIcon />} onClick={handleSendMessage}>
            Send
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default App;
