import React, { useState, useRef, useEffect } from 'react';
import { Container, Box, Typography, TextField, Button, Paper } from '@mui/material'; // Diese Zeile importiert Button
import Message from './Message';
import SendIcon from '@mui/icons-material/Send'; // Senden-Icon

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null); // Referenz auf das Ende der Nachrichtenbox

  // Funktion zum automatischen Scrollen nach unten
  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleSendMessage = () => {
    if (input.trim() !== '') {
      setMessages((prevMessages) => [...prevMessages, { text: input, sender: 'user' }]);
      setInput('');
      setInput('dhhdj')

      // Simulate bot response
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: "I'm a chatbot! How can I assist you?", sender: 'bot' },
        ]);
      }, 1000);
    }
  };

  // Scroll nach unten, wenn neue Nachrichten hinzugefügt werden
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Funktion zum Handhaben der Enter-Taste
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault(); // Verhindert das Absenden von Formularen (wenn ein Formular existiert)
      handleSendMessage(); // Ruft die gleiche Funktion auf, die der Button ausführt
    }
  };

  return (
    <Container
      style={{
        display: 'flex',
        flexDirection: 'column',
        height: '100vh', // Volle Höhe des Fensters
        width: '100vw', // Volle Breite des Fensters (viewport width)
        padding: '0', // Kein zusätzlicher Abstand
        margin: '0', // Kein zusätzlicher Rand
      }}
    >
      <Paper
        style={{
          display: 'flex',
          flexDirection: 'column',
          height: '100%', // Volle Höhe der Paper-Komponente
          width: '100%', // Volle Breite der Paper-Komponente
          padding: '20px',
          boxSizing: 'border-box', // Wichtige Eigenschaft für korrekte Berechnung
        }}
      >
        <Typography variant="h4" gutterBottom align="center">
          SüdtirolKI
        </Typography>

        {/* Box für die Nachrichten */}
        <Box
          style={{
            flex: 1,
            overflowY: 'auto', // Scrollbar bei zu vielen Nachrichten
            marginBottom: '16px', // Abstand zwischen Nachrichten und Textfeld
          }}
        >
          {messages.map((msg, index) => (
            <Message key={index} message={msg} />
          ))}
          <div ref={messagesEndRef} /> {/* Verstecktes Element zum Scrollen */}
        </Box>

        {/* Box für das Textfeld und den Button */}
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <TextField
            fullWidth
            variant="outlined"
            label="Type a message"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown} // Hinzufügen der Event-Listener für die Enter-Taste
            style={{ marginRight: '10px' }}
          />
          <Button
            variant="contained"
            color="primary"
            startIcon={<SendIcon />}
            style={{ padding: '14px 24px', fontSize: '16px' }} // Hier kannst du die Größe anpassen
            onClick={handleSendMessage}
          >
            Send
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default App;
