import React, { useState, useRef, useEffect } from 'react';
import { Container, Box, Typography, TextField, Button, Paper, ThemeProvider, createTheme } from '@mui/material';
import Message from './Message';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const handleSendMessage = () => {
    if (input.trim() !== '') {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');

      // Simulate bot response
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: "I'm a chatbot! How can I assist you?", sender: 'bot' },
        ]);
      }, 1000);
    }
  };

  // Automatically scroll to the latest message
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Dark theme configuration
  const theme = createTheme({
    palette: {
      mode: 'dark', // Activate dark mode
    },
  });

  return (
    <ThemeProvider theme={theme}>
      <Container
        style={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
          height: '100vh',
          paddingTop: '20px',
          width: '100%', // Vollständige Breite auf allen Bildschirmen
          maxWidth: '1200px', // Maximale Breite für sehr große Displays
          margin: '0 auto', // Zentriert auf dem Bildschirm
        }}
>

        <Paper
          style={{
            display: 'flex',
            flexDirection: 'column',
            flexGrow: 1,
            padding: '20px',
            overflow: 'hidden',
          }}
        >
          <Typography variant="h4" gutterBottom align="center">
            Chatbot
          </Typography>
          <Box
            style={{
              flexGrow: 1,
              overflowY: 'auto',
              marginBottom: '10px',
            }}
          >
            {messages.map((msg, index) => (
              <Message key={index} message={msg} />
            ))}
            <div ref={messagesEndRef} />
          </Box>
        </Paper>
        <Box
          style={{
            display: 'flex',
            alignItems: 'center',
            padding: '10px 0',
            borderTop: '1px solid #ccc',
            background: '#333', // Dark background for the input area
          }}
        >
          <TextField
            fullWidth
            variant="outlined"
            label="Type a message"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            style={{ marginRight: '10px' }}
            InputLabelProps={{
              style: { color: '#fff' }, // White label for dark mode
            }}
            InputProps={{
              style: { color: '#fff' }, // White text color for dark mode
            }}
          />
          <Button variant="contained" color="primary" onClick={handleSendMessage}>
            Send
          </Button>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
